{"changed":true,"filter":false,"title":"helpers.php","tooltip":"/common/helpers.php","value":"<?php\n/**\n * Contains PHP helper functions\n**/\n\n// Turns on detailed error reporting\nerror_reporting(E_ALL); \nini_set(\"display_errors\", 1);\nsession_start_once();\n\n// Autoloads models\nspl_autoload_register(function($class_name) {\n    $file = __DIR__ . \"/../models/\" . $class_name. \".php\";\n    if (file_exists($file)) {\n        include_once($file);\n    }\n});\n\n// Autoload common files\nspl_autoload_register(function($class_name) {\n    $file = __DIR__ . \"/../common/\" . $class_name. \".php\";\n    if (file_exists($file)) {\n        include_once($file);\n    }\n});\n\n// Autoload anything else\nspl_autoload_register(function($class_name) {\n    $file = __DIR__ . \"/../\" . $class_name. \".php\";\n    if (file_exists($file)) {\n        include_once($file);\n    }\n});\n\n// Only starts a session if it hasn't already been started\nfunction session_start_once() {\n    if (session_id() == '')\n        session_start();\n}\n\n// Wrapper for reading JSON input\nfunction getJSON() {\n    return json_decode(file_get_contents(\"php://input\"));\n}\n\n// Checks for non-null property strings on an object\nfunction hasStrings($obj, $properties) {\n    foreach ($properties as $property) {\n        if (!isset($obj->$property))\n            return false;\n    }\n    return true;\n}\n\n?>","undoManager":{"mark":11,"position":-1,"stack":[[{"start":{"row":35,"column":20},"end":{"row":35,"column":21},"action":"insert","lines":["n"],"id":12}],[{"start":{"row":35,"column":19},"end":{"row":35,"column":20},"action":"insert","lines":["o"],"id":12}],[{"start":{"row":35,"column":18},"end":{"row":35,"column":19},"action":"insert","lines":["i"],"id":13}],[{"start":{"row":35,"column":17},"end":{"row":35,"column":18},"action":"insert","lines":["s"],"id":14}],[{"start":{"row":35,"column":16},"end":{"row":35,"column":17},"action":"insert","lines":["s"],"id":15}],[{"start":{"row":35,"colu